import { Component, OnInit } from '@angular/core';
import { EventService } from 'src/app/services/event.service';
import { Event } from 'src/app/atomes/event';

@Component({
  selector: 'app-acceuil',
  templateUrl: './accueil.component.html',
  styleUrls: ['./accueil.component.scss']
})

export class AccueilComponent implements OnInit {

  //import du tableau des events, nommé EVENTS
  events: Event[]= [];

  //injection EventService
  constructor(private eventService: EventService) { }

  ngOnInit(): void {
    this.getEvents();
  }

  //subscribe pour récupérer le "Observable" en asynchrone
  getEvents(): void {
    this.eventService.getEvents().subscribe(events => this.events = events);
  }
}
<div class="d-flex flex-justify-content-space-around ">
    <div class="component colonne d-flex flex-column  width-50 m-1 p-10">
        <app-event-details-list [arrayEvent]="events"></app-event-details-list>
    </div>
    <div class="width-50 m-1">
        <app-daily-weather>
        </app-daily-weather>
    </div>
</div>
